import DialogItem from "./DialogItem";
import s from "./Dialogs.module.css"
import DialogMessage from "./DialogsMessage";

const Dialogs = (props) => {

	let dialogsData = [
		{id: 1, name: 'Керим'},
		{id: 2, name: 'Ильяс'},
		{id: 3, name: 'Ибраим'},
		{id: 4, name: 'Ахмад'},
		{id: 5, name: 'Аким'},
	]

	let messagesData = [
		{id: 1, message: 'Hello, how are you?'},
		{id: 2, message: 'Hi, i`m good'},
	]
	
	let dialogsElements = dialogsData.map( d => <DialogItem name={d.name} id={d.id}/>) /*Создаем новую переменную в которую присваиваем преобразование массива данных применяя метод .map 
	 далее объявляем стрелочную ф-ию => и пишем компоненту внутри неё прописываем атрибуты name & id которые достаем из названия ф-ии*/

	return (
		<div className={s.dialogs__row}>
			<div className={s.dialogs__items}>
				{dialogsElements}
				{/* <DialogItem name={dialogsData[0].name} id={dialogsData[0].id}/>
				<DialogItem name={dialogsData[1].name} id={dialogsData[1].id}/>
				<DialogItem name={dialogsData[2].name} id={dialogsData[2].id}/>
				<DialogItem name={dialogsData[3].name} id={dialogsData[3].id}/>
				<DialogItem name={dialogsData[4].name} id={dialogsData[4].id}/> */}
			</div>
			<div className={s.dialogs__messages}>
				<DialogMessage message={messagesData[0].message}/>
				<DialogMessage message={messagesData[1].message}/>
			</div>
		</div>
	);
}
export default Dialogs;
