}

const mapDispatchToProps = (dispatch) =>{
	let action = onPostChangeActionCreator(textFromNewPost);
	return{
		addPost: dispatch(addPostActionCreator()),
		onPostChangeActionCreator: dispatch(action),
	}
}


const SuperNewPostContainer = connect(mapStateToProps, mapDispatchToProps)(NewPost);



export default SuperNewPostContainer;
