import "./NewPost.css"
import React from 'react';
const NewPost = (props) => {
                                                                                                                                                                                                              
	let newPostElement = React.createRef();
	let addPost = () =>{
			let textFromNewPost = newPostElement.current.value;
			props.addPost(textFromNewPost);
	}
	return (
		<div className="main-screen__content-inputNews">
			<form action="#" className="main-screen__content-form">
				<div className="main-screen__content-form-wrapper">
					<textarea ref={newPostElement} placeholder="Write something..." className="main-screen__content-textarea">
					</textarea>
					<button onClick={addPost} className="main-screen__content-button">
						Add post
					</button>
				</div>
			</form>
		</div>
	);
}

export default NewPost;
