const messagesReducer = (state, action) => { // 
	switch (action.type){
		case 'UPDATE-MESSAGE-TEXT':
			state.newMessage = action.newMessageText;

			return state;
			
		case 'ADD-MESSAGE':
			let newMessage = {
				id: 3,
				message: state.newMessage,
			}
			state.messagesData.push(newMessage);
			state.newMessage = '';

			return state;

		default:
			return state;
	}
}
export default messagesReducer;
