const profileReducer = (state, action) => { // Изначально под именем state пришел profilePage при удовлетворении условия if он преобразовывается и возвращается через тот-же state
	// rerender происходит не здесь
	switch (action.type) {
		case 'UPDATE-POST-TEXT':
			state.newPostText = action.newText;
			return state;

		case 'ADD-POST':
			let newPost = {
				id: 3,
				likes: 0,
				message: state.newPostText,
			}
			state.postData.unshift(newPost);
			state.newPostText = '';
			return state;

		default:
			return state;
	}
}

export let onPostChangeActionCreator = (textFromNewPost) => {
	return {
		type: 'UPDATE-POST-TEXT',
		newText: textFromNewPost,
	}
}
export let addPostActionCreator = () => {
	return {
		type: 'ADD-POST'
	}
}
export default profileReducer;
