import React from 'react';
import s from "./Dialogs.module.css"
const WritingMessage = () => {
                                                                                                                                                                                                              
	let newMessageElement = React.createRef();
	let addMessage = () =>{
			let textFromNewMessage = newMessageElement.current.value;
			alert(textFromNewMessage);
	}
	return (
		<div className={s.dialogs__messageWr}>
			<form action="#" method="post" className={s.dialogs__messageForm}>
				<div className={s.dialogs__messageTextWrapper}>
					<textarea ref={newMessageElement} className={s.dialogs__messageText}>
					</textarea>
					<button onClick={addMessage} className={s.dialogs__messageButton}>
						Add post
					</button>
				</div>
			</form>
		</div>
	);
}

export default WritingMessage;
