import "./NewPost.css"
import React from 'react';
const addPost = (props) => {
                                                                                                                                                                                                              
	let newPostElement = React.createRef();
	let letterCounter = () =>{
		let textFromNewPost = newPostElement.current.value;
		console.log(textFromNewPost);
		props.addPost(textFromNewPost);
	}
// 	let addPost = () =>{
// 		// textFromNewPost = newPostElement.current.value;
// 		// props.addPost(textFromNewPost);
// 		// newPostElement.current.value = '';
// }

	return (
		<div className="main-screen__content-inputNews">
			<div className="main-screen__content-form">
				<div className="main-screen__content-form-wrapper">
					<textarea onKeyDown={letterCounter} ref={newPostElement} placeholder="Write something..." className="main-screen__content-textarea">
					</textarea>
					<button type="button" onClick={addPost} className="main-screen__content-button">
						Add post
					</button>
				</div>
			</div>
		</div>
	);
}

export default NewPost;
