const filteredElements = messagesElements.map(el => Object.fromEntries(Object.entries(el.props).filter(([key, value]) => value !== undefined)));
console.log(filteredElements[0].messageFromMe, filteredElements[0].messageFromAnother);
console.log(messagesElements);

